// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {WadRayMath} from "./WadRayMath.sol";

library MathUtils {
    using WadRayMath for uint256;

    uint256 internal constant SECONDS_PER_YEAR = 365 days;

    /**
     * @dev Function to calculate the interest accumulated using a linear interest rate formula
     * @param rate The interest rate, in ray
     * @param lastUpdateTimestamp The timestamp of the last update of the interest
     * @return The interest rate linearly accumulated during the timeDelta, in ray
     **/
    function calculateLinearInterest(uint256 rate, uint40 lastUpdateTimestamp)
        internal
        view
        returns (uint256)
    {
        uint256 result = rate * (block.timestamp - uint256(lastUpdateTimestamp));
        unchecked {
            result = result / SECONDS_PER_YEAR;
        }

        return WadRayMath.RAY + result;
    }

    function calculateLinearInterestAdjusted(
        uint256 rate,
        uint40 lastUpdateTimestamp,
        uint256 adjustedDeltaTimestamp
    ) internal view returns (uint256) {
        uint256 result = rate *
            (block.timestamp + adjustedDeltaTimestamp - uint256(lastUpdateTimestamp));
        unchecked {
            result = result / SECONDS_PER_YEAR;
        }

        return WadRayMath.RAY + result;
    }
}

contracts/libraries/WadRayMath.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.19;

/**
 * @title WadRayMath library
 * @author Aave
 * @notice Provides functions to perform calculations with Wad and Ray units
 * @dev Provides mul and div function for wads (decimal numbers with 18 digits of precision) and rays (decimal numbers
 * with 27 digits of precision)
 * @dev Operations are rounded. If a value is >=.5, will be rounded up, otherwise rounded down.
 **/
library WadRayMath {
    // HALF_WAD and HALF_RAY expressed with extended notation as constant with operations are not supported in Yul assembly
    uint256 internal constant WAD = 1e18;
    uint256 internal constant HALF_WAD = 0.5e18;

    uint256 internal constant RAY = 1e27;
    uint256 internal constant HALF_RAY = 0.5e27;

    uint256 internal constant WAD_RAY_RATIO = 1e9;

    /**
     * @dev Multiplies two wad, rounding half up to the nearest wad
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Wad
     * @param b Wad
     * @return c = a*b, in wad
     **/
    function wadMul(uint256 a, uint256 b) internal pure returns (uint256 c) {
        // to avoid overflow, a <= (type(uint256).max - HALF_WAD) / b
        assembly {
            if iszero(or(iszero(b), iszero(gt(a, div(sub(not(0), HALF_WAD), b))))) {
                revert(0, 0)
            }

            c := div(add(mul(a, b), HALF_WAD), WAD)
        }
    }

    /**
     * @dev Divides two wad, rounding half up to the nearest wad
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Wad
     * @param b Wad
     * @return c = a/b, in wad
     **/
    function wadDiv(uint256 a, uint256 b) internal pure returns (uint256 c) {
        // to avoid overflow, a <= (type(uint256).max - halfB) / WAD
        assembly {
            if or(iszero(b), iszero(iszero(gt(a, div(sub(not(0), div(b, 2)), WAD))))) {
                revert(0, 0)
            }

            c := div(add(mul(a, WAD), div(b, 2)), b)
        }
    }

    /**
     * @notice Multiplies two ray, rounding half up to the nearest ray
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Ray
     * @param b Ray
     * @return c = a raymul b
     **/
    function rayMul(uint256 a, uint256 b) internal pure returns (uint256 c) {
        // to avoid overflow, a <= (type(uint256).max - HALF_RAY) / b
        assembly {
            if iszero(or(iszero(b), iszero(gt(a, div(sub(not(0), HALF_RAY), b))))) {
                revert(0, 0)
            }

            c := div(add(mul(a, b), HALF_RAY), RAY)
        }
    }

    /**
     * @notice Divides two ray, rounding half up to the nearest ray
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Ray
     * @param b Ray
     * @return c = a raydiv b
     **/
    function rayDiv(uint256 a, uint256 b) internal pure returns (uint256 c) {
        // to avoid overflow, a <= (type(uint256).max - halfB) / RAY
        assembly {
            if or(iszero(b), iszero(iszero(gt(a, div(sub(not(0), div(b, 2)), RAY))))) {
                revert(0, 0)
            }

            c := div(add(mul(a, RAY), div(b, 2)), b)
        }
    }

    /**
     * @dev Casts ray down to wad
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Ray
     * @return b = a converted to wad, rounded half up to the nearest wad
     **/
    function rayToWad(uint256 a) internal pure returns (uint256 b) {
        assembly {
            b := div(a, WAD_RAY_RATIO)
            let remainder := mod(a, WAD_RAY_RATIO)
            if iszero(lt(remainder, div(WAD_RAY_RATIO, 2))) {
                b := add(b, 1)
            }
        }
    }

    /**
     * @dev Converts wad up to ray
     * @dev assembly optimized for improved gas savings, see https://twitter.com/transmissions11/status/1451131036377571328
     * @param a Wad
     * @return b = a converted in ray
     **/
    function wadToRay(uint256 a) internal pure returns (uint256 b) {
        // to avoid overflow, b/WAD_RAY_RATIO == a
        assembly {
            b := mul(a, WAD_RAY_RATIO)

            if iszero(eq(div(b, WAD_RAY_RATIO), a)) {
                revert(0, 0)
            }
        }
    }
}

contracts/MerkleTree.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/IHasher.sol";
import "./interfaces/IMerkleTree.sol";

contract MerkleTree is IMerkleTree, Initializable {
    uint256 public constant FIELD_SIZE =
        21888242871839275222246405745257275088548364400416034343698204186575808495617;

    uint32 public constant ROOT_HISTORY_SIZE = 100;

    uint256 public constant ZERO_LEAF = uint256(keccak256("privi-yield")) % FIELD_SIZE;

    IHasher public immutable hasher;
    uint256 public immutable numLevels;

    mapping(uint256 => bytes32) public zeroes;
    mapping(uint256 => bytes32) public filledSubtrees;
    mapping(uint256 => bytes32) public roots;

    uint32 public currentRootIndex;
    uint32 public nextLeafIndex;

    constructor(uint256 numLevels_, address hasher_) {
        if (numLevels_ == 0 || numLevels_ >= 32) revert OutOfRangeMerkleTreeDepth(numLevels_);

        numLevels = numLevels_;
        hasher = IHasher(hasher_);
    }

    function __MerkleTree_init() internal onlyInitializing {
        // Calculate the zero nodes
        bytes32 zero = bytes32(ZERO_LEAF);
        for (uint8 i = 0; i < numLevels; ) {
            zeroes[i] = zero;
            filledSubtrees[i] = zero;
            zero = hashLeftRight(zero, zero);

            unchecked {
                ++i;
            }
        }

        roots[0] = zero;
    }

    function hashLeftRight(bytes32 left, bytes32 right) public view returns (bytes32) {
        if (uint256(left) >= FIELD_SIZE) revert InputOutOfFieldSize(left);
        if (uint256(right) >= FIELD_SIZE) revert InputOutOfFieldSize(right);

        bytes32[2] memory input;
        input[0] = left;
        input[1] = right;

        return hasher.poseidon(input);
    }

    function _insert(bytes32 leaf1, bytes32 leaf2) internal returns (uint32 index) {
        uint32 _nextIndex = nextLeafIndex;

        if (_nextIndex >= 2**numLevels) revert MerkleTreeFull();

        uint32 currentIndex = _nextIndex / 2;

        bytes32 currentLevelHash = hashLeftRight(leaf1, leaf2);
        bytes32 left;
        bytes32 right;

        for (uint8 i = 1; i < numLevels; ) {
            if (currentIndex % 2 == 0) {
                left = currentLevelHash;
                right = zeroes[i];
                filledSubtrees[i] = currentLevelHash;
            } else {
                left = filledSubtrees[i];
                right = currentLevelHash;
            }
            currentLevelHash = hashLeftRight(left, right);
            currentIndex /= 2;

            unchecked {
                ++i;
            }
        }

        uint32 newRootIndex = (currentRootIndex + 1) % ROOT_HISTORY_SIZE;
        currentRootIndex = newRootIndex;
        roots[newRootIndex] = currentLevelHash;

        nextLeafIndex = _nextIndex + 2;
        return _nextIndex;
    }

    function isKnownRoot(bytes32 root) public view returns (bool) {
        if (root == 0) {
            return false;
        }
        uint32 _currentRootIndex = currentRootIndex;
        uint256 i = _currentRootIndex;
        do {
            if (root == roots[i]) {
                return true;
            }
            if (i == 0) {
                i = ROOT_HISTORY_SIZE;
            }
            --i;
        } while (i != _currentRootIndex);
        return false;
    }

    function getLastRoot() public view returns (bytes32) {
        return roots[currentRootIndex];
    }

    uint256[46] __gap;
}

contracts/Pool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "./interfaces/IVerifier.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IAavePool.sol";
import "./interfaces/IAavePoolAddressProvider.sol";
import "./interfaces/IAToken.sol";
import "./MerkleTree.sol";
import "./base/Compliance.sol";
import {TxType, ProofArgs, ExtData, AaveReserveData} from "./helpers/DataTypes.sol";
import {WadRayMath} from "./libraries/WadRayMath.sol";
import {MathUtils} from "./libraries/MathUtils.sol";

contract Pool is
    IPool,
    Initializable,
    UUPSUpgradeable,
    OwnableUpgradeable,
    MerkleTree,
    Compliance,
    ReentrancyGuardUpgradeable
{
    using WadRayMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 public immutable token;
    IAavePoolAddressProvider public immutable aavePoolAddressProvider;

    IVerifier public immutable verifier2;
    IVerifier public immutable verifier16;

    uint256 public maxSupplyAmount;

    mapping(bytes32 => bool) public nullifierHashes;

    constructor(
        uint32 numLevels_,
        IERC20 token_,
        IAavePoolAddressProvider aavePoolAddressProvider_,
        address hasher_,
        IVerifier verifier2_,
        IVerifier verifier16_,
        address sanctionsList_
    ) MerkleTree(numLevels_, hasher_) Compliance(sanctionsList_) {
        token = token_;
        aavePoolAddressProvider = aavePoolAddressProvider_;
        verifier2 = verifier2_;
        verifier16 = verifier16_;
    }

    function initialize(uint256 maxSupplyAmount_) external initializer {
        maxSupplyAmount = maxSupplyAmount_;
        __MerkleTree_init();
        __Ownable_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();
    }

    function supply(
        ProofArgs calldata args,
        ExtData calldata extData
    ) external onlyNonSanctioned returns (uint256) {
        (address aavePoolAddress, , uint256 nextLiquidityIndex) = getAavePoolAndReserveData();

        uint256 supplyAmount = extData.scaledAmount.rayMul(nextLiquidityIndex);

        if (supplyAmount > maxSupplyAmount) {
            revert SupplyExceedsMaxLimit(supplyAmount, maxSupplyAmount);
        }

        token.safeTransferFrom(msg.sender, address(this), supplyAmount);
        token.approve(aavePoolAddress, supplyAmount);
        IAavePool(aavePoolAddress).supply(address(token), supplyAmount, address(this), 0);

        _transact(args, extData, TxType.SUPPLY);

        return supplyAmount;
    }

    function withdraw(
        ProofArgs calldata args,
        ExtData calldata extData
    ) external onlyNonSanctioned returns (uint256) {
        if (extData.recipient == address(0)) revert ZeroRecipientAddress();

        _transact(args, extData, TxType.WITHDRAW);

        (
            address aavePoolAddress,
            AaveReserveData memory aaveReserveData,

        ) = getAavePoolAndReserveData();

        uint256 normalizedIncome = getAaveReserveNormalizedIncome();

        uint256 withdrawAmount = extData.scaledAmount.rayMul(normalizedIncome);
        uint256 fee = extData.scaledFee.rayMul(normalizedIncome);

        IAToken(aaveReserveData.aTokenAddress).approve(aavePoolAddress, withdrawAmount);
        IAavePool(aavePoolAddress).withdraw(address(token), withdrawAmount + fee, address(this));

        token.safeTransfer(extData.recipient, withdrawAmount);
        if (fee > 0) {
            token.safeTransfer(extData.relayer, fee);
        }

        return withdrawAmount;
    }

    function transfer(
        ProofArgs calldata args,
        ExtData calldata extData
    ) external onlyNonSanctioned {
        _transact(args, extData, TxType.TRANSFER);

        if (extData.scaledFee > 0) {
            (
                address aavePoolAddress,
                AaveReserveData memory aaveReserveData,

            ) = getAavePoolAndReserveData();

            uint256 normalizedIncome = IAavePool(aavePoolAddress).getReserveNormalizedIncome(
                address(token)
            );
            uint256 fee = extData.scaledFee.rayMul(normalizedIncome);

            IAToken(aaveReserveData.aTokenAddress).approve(aavePoolAddress, fee);
            token.safeTransfer(extData.relayer, fee);
        }
    }

    function getAavePoolAndReserveData()
        public
        view
        returns (address, AaveReserveData memory, uint256)
    {
        address aavePoolAddress = aavePoolAddressProvider.getPool();
        AaveReserveData memory reserveData = IAavePool(aavePoolAddress).getReserveData(
            address(token)
        );

        uint256 cumulatedLiquidityInterest = MathUtils.calculateLinearInterest(
            reserveData.currentLiquidityRate,
            reserveData.lastUpdateTimestamp
        );
        uint256 nextLiquidityIndex = cumulatedLiquidityInterest.rayMul(reserveData.liquidityIndex);
        return (aavePoolAddress, reserveData, nextLiquidityIndex);
    }

    function getAaveReserveNormalizedIncome() public view returns (uint256) {
        address aavePoolAddress = aavePoolAddressProvider.getPool();
        return IAavePool(aavePoolAddress).getReserveNormalizedIncome(address(token));
    }

    // function getBalance(uint256 scaledAmount) public view returns (uint256) {
    //     uint256 normalizedIncome = getAaveReserveNormalizedIncome();
    //     uint256 balance = scaledAmount.rayMul(normalizedIncome);
    //     return balance;
    // }

    // function getAaveNextLiquidityIndex() public view returns (uint256) {
    //     (, , uint256 nextLiquidityIndex) = getAavePoolAndReserveData();
    //     return nextLiquidityIndex;
    // }

    // function getAaveScaledAmount(uint256 amount) public view returns (uint256) {
    //     address aavePoolAddress = aavePoolAddressProvider.getPool();
    //     AaveReserveData memory reserveData = IAavePool(aavePoolAddress).getReserveData(
    //         address(token)
    //     );
    //     uint256 cumulatedLiquidityInterest = MathUtils.calculateLinearInterest(
    //         reserveData.currentLiquidityRate,
    //         reserveData.lastUpdateTimestamp
    //     );
    //     uint256 nextLiquidityIndex = cumulatedLiquidityInterest.rayMul(reserveData.liquidityIndex);
    //     uint256 scaledAmount = amount.rayDiv(nextLiquidityIndex);

    //     return scaledAmount;
    // }

    // function getAaveScaledAmountAdjusted(
    //     uint256 amount,
    //     uint256 deltaSec
    // ) public view returns (uint256, uint256) {
    //     address aavePoolAddress = aavePoolAddressProvider.getPool();
    //     AaveReserveData memory reserveData = IAavePool(aavePoolAddress).getReserveData(
    //         address(token)
    //     );
    //     uint256 cumulatedLiquidityInterest = MathUtils.calculateLinearInterestAdjusted(
    //         reserveData.currentLiquidityRate,
    //         reserveData.lastUpdateTimestamp,
    //         deltaSec
    //     );
    //     uint256 nextLiquidityIndex = cumulatedLiquidityInterest.rayMul(reserveData.liquidityIndex);
    //     uint256 scaledAmount = amount.rayDiv(nextLiquidityIndex);
    //     return (scaledAmount, nextLiquidityIndex);
    // }

    function isSpent(bytes32 nullifierHash) public view returns (bool) {
        return nullifierHashes[nullifierHash];
    }

    function getPublicScaledAmount(
        TxType txType,
        uint256 extScaledAmount,
        uint256 scaledFee
    ) public pure returns (uint256) {
        if (txType == TxType.SUPPLY) {
            require(extScaledAmount >= scaledFee, "Ext amount less than scaledFee");
        }

        uint256 publicScaledAmount;

        if (txType == TxType.SUPPLY) {
            publicScaledAmount = extScaledAmount;
        } else if (txType == TxType.WITHDRAW) {
            publicScaledAmount = FIELD_SIZE - (extScaledAmount + scaledFee);
        } else if (txType == TxType.TRANSFER) {
            publicScaledAmount = scaledFee == 0 ? 0 : (FIELD_SIZE - scaledFee);
        } else {
            revert("Invalid TxType");
        }

        return publicScaledAmount;
    }

    function verifyProof(ProofArgs calldata args) public view returns (bool) {
        if (args.inputNullifiers.length == 2) {
            return
                verifier2.verifyProof(
                    args.proof.a,
                    args.proof.b,
                    args.proof.c,
                    [
                        uint256(args.root),
                        args.publicScaledAmount,
                        uint256(args.extDataHash),
                        uint256(args.inputNullifiers[0]),
                        uint256(args.inputNullifiers[1]),
                        uint256(args.outputCommitments[0]),
                        uint256(args.outputCommitments[1])
                    ]
                );
        } else if (args.inputNullifiers.length == 16) {
            return
                verifier16.verifyProof(
                    args.proof.a,
                    args.proof.b,
                    args.proof.c,
                    [
                        uint256(args.root),
                        args.publicScaledAmount,
                        uint256(args.extDataHash),
                        uint256(args.inputNullifiers[0]),
                        uint256(args.inputNullifiers[1]),
                        uint256(args.inputNullifiers[2]),
                        uint256(args.inputNullifiers[3]),
                        uint256(args.inputNullifiers[4]),
                        uint256(args.inputNullifiers[5]),
                        uint256(args.inputNullifiers[6]),
                        uint256(args.inputNullifiers[7]),
                        uint256(args.inputNullifiers[8]),
                        uint256(args.inputNullifiers[9]),
                        uint256(args.inputNullifiers[10]),
                        uint256(args.inputNullifiers[11]),
                        uint256(args.inputNullifiers[12]),
                        uint256(args.inputNullifiers[13]),
                        uint256(args.inputNullifiers[14]),
                        uint256(args.inputNullifiers[15]),
                        uint256(args.outputCommitments[0]),
                        uint256(args.outputCommitments[1])
                    ]
                );
        } else {
            revert("Unsupported input count");
        }
    }

    function _transact(
        ProofArgs calldata args,
        ExtData calldata extData,
        TxType txType
    ) internal nonReentrant {
        if (!isKnownRoot(args.root)) {
            revert InvalidMerkleRoot();
        }

        for (uint256 i = 0; i < args.inputNullifiers.length; ++i) {
            if (isSpent(args.inputNullifiers[i])) {
                revert InputNullifierAlreadySpent();
            }
        }

        uint256 calcExtDataHash = uint256(keccak256(abi.encode(extData))) % FIELD_SIZE;
        if (calcExtDataHash != uint256(args.extDataHash)) {
            revert InvalidExtDataHash();
        }

        uint256 calculatedPublicScaledAmount = getPublicScaledAmount(
            txType,
            extData.scaledAmount,
            extData.scaledFee
        );

        if (calculatedPublicScaledAmount != args.publicScaledAmount) {
            revert InvalidPublicScaledAmount();
        }

        if (!verifyProof(args)) {
            revert InvalidTxProof();
        }

        for (uint256 i = 0; i < args.inputNullifiers.length; ++i) {
            nullifierHashes[args.inputNullifiers[i]] = true;
        }

        _insert(args.outputCommitments[0], args.outputCommitments[1]);

        emit CommitmentInserted(
            args.outputCommitments[0],
            nextLeafIndex - 2,
            extData.encryptedOutput1
        );
        emit CommitmentInserted(
            args.outputCommitments[1],
            nextLeafIndex - 1,
            extData.encryptedOutput2
        );
        for (uint256 i = 0; i < args.inputNullifiers.length; i++) {
            emit NullifierUsed(args.inputNullifiers[i]);
        }
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/WTokenGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IWToken.sol";
import "./interfaces/IPool.sol";
import "./interfaces/IWTokenGateway.sol";
import {WadRayMath} from "./libraries/WadRayMath.sol";
import {ProofArgs, ExtData, AaveReserveData} from "./helpers/DataTypes.sol";

contract WTokenGateway is IWTokenGateway {
    using WadRayMath for uint256;
    IWToken public immutable wToken;

    constructor(address wToken_) {
        wToken = IWToken(wToken_);
    }

    function supply(
        address pool,
        ProofArgs calldata args,
        ExtData calldata extData
    ) external payable {
        (, , uint256 nextLiquidityIndex) = IPool(pool).getAavePoolAndReserveData();

        uint256 supplyAmount = extData.scaledAmount.rayMul(nextLiquidityIndex);

        if (msg.value < supplyAmount) {
            revert NotEnoughTokensSent(msg.value, supplyAmount);
        }

        wToken.deposit{value: supplyAmount}();
        wToken.approve(pool, supplyAmount);

        IPool(pool).supply(args, extData);

        // Refund change back to the sender
        uint256 changeAmount = msg.value - supplyAmount;
        if (changeAmount != 0) {
            _safeTransferETH(msg.sender, changeAmount);
        }
    }

    function withdraw(
        address pool,
        address unwrappedTokenReceiver,
        ProofArgs calldata args,
        ExtData calldata extData
    ) external {
        if (extData.recipient != address(this)) {
            revert RecipientNotGateway(extData.recipient, address(this));
        }

        uint256 withdrawAmount = IPool(pool).withdraw(args, extData);

        wToken.approve(address(wToken), withdrawAmount);
        wToken.withdraw(withdrawAmount);

        _safeTransferETH(unwrappedTokenReceiver, withdrawAmount);
    }

    function _safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, "ETH_TRANSFER_FAILED");
    }

    receive() external payable {}
}
